mapper.select:
SELECT PI.PRO_INQ_ID AS inquiryId, PI.BYR_ID AS buyerId, CAST(PI.INQ_TYPE AS INTEGER) AS inquiryType, PI.CONTENT AS content, PI.STATUS AS status, PI.CRE_DATE AS createdDate, PI.PDT_ID AS productId, PI.IS_SECRET AS isSecret, PR.PRO_RES_ID AS responseId, PR.CONTENT AS responseContent, PR.CRE_DATE AS responseDate, PII.IMAGE_PATH AS imagePath FROM PRO_INQUIRIES PI LEFT JOIN PRO_RESPONSES PR ON PI.PRO_INQ_ID = PR.PRO_INQ_ID LEFT JOIN PRO_INQUIRY_IMAGES PII ON PI.PRO_INQ_ID = PII.PRO_INQ_ID WHERE PI.BYR_ID = #{buyerId}
UNION ALL
SELECT GI.GEN_INQ_ID AS inquiryId, GI.BYR_ID AS buyerId, CAST(GI.INQ_TYPE AS INTEGER) AS inquiryType, GI.CONTENT AS content, GI.STATUS AS status, GI.CRE_DATE AS createdDate, NULL AS productId, NULL AS isSecret, GR.GEN_RES_ID AS responseId, GR.CONTENT AS responseContent, GR.CRE_DATE AS responseDate, GII.IMAGE_PATH AS imagePath FROM GEN_INQUIRIES GI LEFT JOIN GEN_RESPONSES GR ON GI.GEN_INQ_ID = GR.GEN_INQ_ID LEFT JOIN GEN_INQUIRY_IMAGES GII ON GI.GEN_INQ_ID = GII.GEN_INQ_ID WHERE GI.BYR_ID = #{buyerId}
ORDER BY createdDate DESC
